import { __decorate } from "tslib";
import { HttpParams } from '@angular/common/http';
import { Component } from '@angular/core';
let AddProductComponent = class AddProductComponent {
    constructor(cityService, categoryService, productService) {
        this.cityService = cityService;
        this.categoryService = categoryService;
        this.productService = productService;
        this.title = "qefc";
        this.descrioption = "qqq";
        this.price = 1;
        this.cityId = 0;
        this.areaId = 0;
        this.categoryId = 0;
        this.subcategoryId = 0;
        this.userId = 0;
        //location
        this.cities = [];
        this.areas = [];
        this.isAreasDisabled = true;
        //categories
        this.categories = [];
        this.subcategories = [];
        this.isSubcategoriesDisabled = true;
        //product data
        this.image = null;
        this.fileName = '';
        this.formData = new FormData();
        //newProduct:any={}
        this.product = {};
    }
    ngOnInit() {
        let userData = localStorage.getItem('userData');
        let user = userData != null ? JSON.parse(userData) : null;
        this.userId = user != null ? user.userId : 0;
        let CitiesParams = new HttpParams();
        CitiesParams = CitiesParams.append('expand', 'Areas');
        this.cityService.getAll(CitiesParams).subscribe(data => {
            this.cities = data;
        });
        let CategoriesParams = new HttpParams();
        CategoriesParams = CategoriesParams.append('expand', 'Subcategories');
        this.categoryService.getall(CategoriesParams).subscribe(data => {
            this.categories = data;
        });
    }
    //location
    onSelectCity() {
        if (this.cityId == 0) {
            this.isAreasDisabled = true;
            this.areas = [];
        }
        else {
            let city = this.cities.find(c => c.cityId == this.cityId);
            if (city) {
                this.isAreasDisabled = false;
                this.areas = city.areas;
            }
        }
    }
    //category
    onSelectCategory() {
        if (this.categoryId == 0) {
            this.isSubcategoriesDisabled = true;
            this.subcategories = [];
        }
        else {
            let category = this.categories.find(c => c.categoryId == this.categoryId);
            if (category) {
                this.isSubcategoriesDisabled = false;
                this.subcategories = category.subcategories;
            }
        }
    }
    onFileSelected(event) {
        this.image = event.target.files[0];
        if (this.image) {
            this.fileName = this.image.name;
            console.log(this.image);
            // this.newProduct.append('image',event.target.files[0])
        }
    }
    async addProduct() {
        this.product.title = this.title;
        this.product.descrioption = this.descrioption;
        this.product.price = this.price;
        this.product.cityId = this.cityId;
        this.product.areaId = this.areaId;
        this.product.subcategoryId = this.subcategoryId;
        this.product.userId = this.userId;
        // this.newProduct.product =this.product
        // this.newProduct.image =this.image
        // this.formData.append('title', this.title);
        // this.formData.append('descrioption', this.descrioption);
        // this.formData.append('price', this.price.toString());
        // this.formData.append('cityId', this.cityId.toString());
        // this.formData.append('areaId', this.areaId.toString());
        // this.formData.append('subcategoryId', this.subcategoryId.toString());
        // this.formData.append('userId', this.userId.toString());
        // this.formData.append("image", this.file!);
        //.formData.append('product',this.product)
        // console.log(this.formData.getAll)
        // this.formData.forEach(s=>console.log(s))
        // if(this.formData){
        console.log(this.product);
        await this.productService.addProduct(this.product, this.image).subscribe((product) => {
            //this.productService.addProduct(this.formData).subscribe((product) => {
            console.log(product);
        });
        // }
    }
};
